export default function camelToConst (str) {
  let ret = ''
  let prevLowercase = false
  for (let s of str) {
    const isUppercase = s.toUpperCase() === s
    if (isUppercase && prevLowercase) {
      ret += '_'
    }
    ret += s
    prevLowercase = !isUppercase
  }
  return ret.replace(/_+/g, '_').toUpperCase()
}
