/**
 * Pass the result of each function to the next, from right to left.
 * @return {function} Returns a function that accepts the initial input
 */
export default function compose () {
  if (!arguments || !arguments.length) {
    return
  }
  let args = arguments
  let len = args.length
  return function (input) {
    for (let i = len; i >= 0; i--) {
      if (!args[i]) continue
      input = args[i](input)
    }
    return input
  }
}
