export default function cosntToCamel (str) {
  let ret = ''
  let prevUnderscore = false
  for (let s of str) {
    const isUnderscore = s === '_'
    if (isUnderscore) {
      prevUnderscore = true
      continue
    }
    if (!isUnderscore && prevUnderscore) {
      ret += s
      prevUnderscore = false
    } else {
      ret += s.toLowerCase()
    }
  }
  return ret
}
